```
[
    // move
    { "keys": ["alt+k"], "command": "move", "args": {"by": "lines", "forward": false} },
    { "keys": ["alt+j"], "command": "move", "args": {"by": "lines", "forward": true} },
    { "keys": ["alt+h"], "command": "move", "args": {"by": "characters", "forward": false} },
    { "keys": ["alt+l"], "command": "move", "args": {"by": "characters", "forward": true} },
    { "keys": ["alt+f"], "command": "move", "args": {"by": "words", "forward": true} },
    { "keys": ["alt+b"], "command": "move", "args": {"by": "words", "forward": false} },
    { "keys": ["alt+a"], "command": "move_to", "args": {"to": "bol", "extend": false} },
    { "keys": ["alt+e"], "command": "move_to", "args": {"to": "eol", "extend": false} },
    { "keys": ["alt+v"], "command": "move", "args": {"by": "pages", "forward": true} },
    { "keys": ["alt+shift+v"], "command": "move", "args": {"by": "pages", "forward": false} },
    { "keys": ["alt+m"], "command": "move_to", "args": {"to": "brackets"} },

    // select
    { "keys": ["alt+shift+k"], "command": "move", "args": {"by": "lines", "forward": false, "extend": true} },
    { "keys": ["alt+shift+j"], "command": "move", "args": {"by": "lines", "forward": true, "extend": true} },
    { "keys": ["alt+shift+h"], "command": "move", "args": {"by": "characters", "forward": false, "extend": true} },
    { "keys": ["alt+shift+l"], "command": "move", "args": {"by": "characters", "forward": true, "extend": true} },
    { "keys": ["alt+shift+f"], "command": "move", "args": {"by": "words", "forward": true, "extend": true} },
    { "keys": ["alt+shift+b"], "command": "move", "args": {"by": "words", "forward": false, "extend": true} },
    { "keys": ["alt+shift+a"], "command": "move_to", "args": {"to": "bol", "extend": true} },
    { "keys": ["alt+shift+e"], "command": "move_to", "args": {"to": "eol", "extend": true} },
    { "keys": ["alt+shift+m"], "command": "expand_selection", "args": {"to": "brackets"} },

    // operation
    { "keys": ["alt+d"], "command": "cut" },
    { "keys": ["alt+n"], "command": "left_delete" },
    { "keys": ["alt+shift+n"], "command": "delete_word", "args": { "forward": false } },
    { "keys": ["alt+x"], "command": "right_delete" },
    { "keys": ["alt+shift+x"], "command": "delete_word", "args": { "forward": true } },

    { "keys": ["alt+u"], "command": "undo" },
    { "keys": ["alt+r"], "command": "redo" },
    { "keys": ["alt+y"], "command": "copy" },
    { "keys": ["alt+p"], "command": "paste" },
    { "keys": ["alt+s"], "command": "save" },
    { "keys": ["alt+i"], "command": "find_under_expand" },
    { "keys": ["alt+/"], "command": "toggle_comment", "args": { "block": false } },
    { "keys": ["alt+o"], "command": "run_macro_file", "args": {"file": "res://Packages/Default/Add Line.sublime-macro"} },
    { "keys": ["alt+g"], "command": "show_panel", "args": {"panel": "find", "reverse": false} },
    { "keys": ["alt+g"], "command": "hide_panel", "args": {"cancel": true},
        "context":
        [
            { "key": "panel_visible", "operator": "equal", "operand": true }
        ]
    },

    { "keys": ["alt+shift+p"], "command": "show_overlay", "args": {"overlay": "command_palette"} },
    { "keys": ["alt+t"], "command": "show_overlay", "args": {"overlay": "goto", "show_files": true} },
    { "keys": ["alt+z"], "command": "show_overlay", "args": {"overlay": "goto", "text": "@"} },
    // QQ key Conflict
    { "keys": ["alt+w"], "command": "close" },
    { "keys": ["alt+q"], "command": "select_all" },

    // escape
    { "keys": ["alt+c"], "command": "single_selection", "context":
        [
            { "key": "num_selections", "operator": "not_equal", "operand": 1 }
        ]
    },
    { "keys": ["alt+c"], "command": "clear_fields", "context":
        [
            { "key": "has_next_field", "operator": "equal", "operand": true }
        ]
    },
    { "keys": ["alt+c"], "command": "clear_fields", "context":
        [
            { "key": "has_prev_field", "operator": "equal", "operand": true }
        ]
    },
    { "keys": ["alt+c"], "command": "hide_panel", "args": {"cancel": true},
        "context":
        [
            { "key": "panel_visible", "operator": "equal", "operand": true }
        ]
    },
    { "keys": ["alt+c"], "command": "hide_overlay", "context":
        [
            { "key": "overlay_visible", "operator": "equal", "operand": true }
        ]
    },
    { "keys": ["alt+c"], "command": "hide_popup", "context":
        [
            { "key": "popup_visible", "operator": "equal", "operand": true }
        ]
    },
    { "keys": ["alt+c"], "command": "hide_auto_complete", "context":
        [
            { "key": "auto_complete_visible", "operator": "equal", "operand": true }
        ]
    }
]
```

